script:
frappe.ui.form.on('Lead', {
	after_save: function(frm){
	    frappe.call({
	        method: 'proman.config.notification.lead_notify_on_save',
	        args: {
	            'recipients': frm.doc.contact_by,
	            'phone': frm.doc.phone,
	            'mobile_no': frm.doc.mobile_no
	        },
	        callback: function(r){}
	    })
	}
})

frappe.ui.form.on('Purchase Receipt', {
	on_submit: function(frm, cdt, cdn){
	    frappe.call({
	        method:'proman.config.notification.purchase_receipt_on_submit',
	        args:{
	            "doc":frm.doc
	        },
	        callback:function(r){}
	    })
	}
})
